//import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import { Blockquote } from './Blockquote';
import quotes from './Quotes'
import backgrounds from './Backgrounds'

export default class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      background: "secondary",
      quoteindex: 0,
      quote: {},

    }
  }

  componentDidMount(){
    setTimeout(5000);

    this.setState({
      background: "warning",
    });    
  }

  getNextQuote = ()=>{
    const quoteIndex = Math.floor(Math.random()*quotes.length);
    const bgIndex = Math.floor(Math.random()*backgrounds.length);
    this.setState({
      index: quoteIndex,
      background: bgIndex
    }) 
  }

  render(){
    let bg = this.state.background;
    return (
      <div style={{height: '100vh'}} id="outer-wrapper" className={`bg-${bg} text-center text-white m-0`}>
          <div id="quote-box" className="bg-white">
              <div id="quote">
                <q id="text">
                  {quotes[this.state.index].quote}
                </q>
                <cite id="author">{quotes[this.state.index].quoter}</cite>
              </div>
              <div>
                <a id="tweet-quote"  target="_blank"
                    href="https://twitter.com/intent/tweet">
                  tweet
                </a>
                <a id="facebook-quote" target="_top"
                  href="https://facebook.com"
                >
                  facebook
                </a>
                <button 
                 className={`btn btn-${bg}`}
                 id="new-quote" onClick={ this.getNextQuote }>
                  New Quote
                </button>
              </div>
          </div>

          <footer id="footer" className="">
            <p>by Musekwa</p>
          </footer>
      </div>
    );
  }
}
