//import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import { Blockquote } from './Blockquote';
import quotes from './Quotes'
import backgrounds from './Backgrounds'

export default class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      background: "secondary",
      index: 0,
      quote: {},

    }
  }

  componentDidMount(){
    setTimeout(5000);

    this.setState({
      background: "info",
    });    
  }

  getNextQuote = ()=>{
    const newIndex = Math.floor(Math.random()*quotes.length);
    this.setState({
      index: newIndex,
    }) 
  }

  render(){
    let bg = this.state.background;
    return (
      <div id="outer-wrapper" className={`bg-${bg} text-center text-white`}>
          <div id="quote-box">
              <q id="text">
                {quotes[this.state.index].quote}
              </q>
              <cite id="author">{quotes[this.state.index].quoter}</cite>
              <a id="tweet-quote" 
                  target="_blank"
                  href="https://twitter.com/intent/tweet">
                tweet
              </a>
              <div>
                <button id="new-quote" onClick={ this.getNextQuote }>
                  New Quote
                </button>
              </div>
          </div>

          <div className="text">
            <p>by Musekwa</p>
          </div>
      </div>
    );
  }
}
