//import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import { Blockquote } from './Blockquote';
import quotes from './Quotes'
import backgrounds from './Backgrounds'

export default class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      qt_index: 0,
      bg_index: 0      
    }
  }

  componentDidMount(){

    this.setState({
      background: "secondary",

    });    
  }

  getRandoms = ()=>{
    return {
      bg: Math.floor(Math.random()*backgrounds.length),
      qt: Math.floor(Math.random()*quotes.length)
    }
  }

  getNextQuote = ()=>{
    this.setState({
      qt_index: this.getRandoms.qt,
      bg_index: this.getRandoms.bg
    }) 
  }

  render(){
    let bg = this.state.background;
    return (
      <div style={{height: '100vh'}} id="outer-wrapper" className={`bg-${bg} text-center text-white m-0`}>
          <div id="quote-box" className="bg-white">
              <div id="quote">
                <q id="text">
                  {quotes[this.state.qt_index].quote}
                </q>
                <cite id="author">{quotes[this.state.qut_index].quoter}</cite>
              </div>
              <div>
                <a id="tweet-quote"  target="_blank"
                    href="https://twitter.com/intent/tweet">
                  tweet
                </a>
                <a id="facebook-quote" target="_top"
                  href="https://facebook.com"
                >
                  facebook
                </a>
                <button 
                 className={`btn btn-${bg}`}
                 id="new-quote" onClick={ this.getNextQuote }>
                  New Quote
                </button>
              </div>
          </div>

          <footer id="footer" className="">
            <p>by Musekwa</p>
          </footer>
      </div>
    );
  }
}
