//import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import extra from './ExternalModules'
import { FaFacebookSquare, FaTwitter } from "react-icons/fa";

export default class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      qt_index: extra.randoms().qt,
      bg_index: extra.randoms().bg      
    }
  }

  componentDidMount(){

    this.setState({
      background: "secondary",

    });    
  }



  getNextQuote = ()=>{
    setTimeout(2000);
    this.setState({
      qt_index: extra.randoms().qt,
      bg_index: extra.randoms().bg
    }); 
  }

  render(){
    let bg = extra.backgrounds[this.state.bg_index];
    return (
      <div style={{height: '100vh'}} id="outer-wrapper" className={`bg-${bg} text-center m-0`}>
          <div id="quote-box" className="bg-white">
              <div id="quote">
                <q id="text" className={`text-${bg}`}>
                  {extra.quotes[this.state.qt_index].quote}
                </q>
                <p className={`text-${bg}`} id="author">-{extra.quotes[this.state.qt_index].quoter}</p>
              </div>
              <div className="container">
                <div className="">
                  <a className={`text-${bg}`} id="tweet-quote"  target="_blank"
                      href="https://twitter.com/intent/tweet">
                     <FaTwitter />
                  </a>
                </div>
                <div>
                  <a className={`text-${bg}`} target="_top"
                    href="https://facebook.com" >
                    <FaFacebookSquare />
                  </a>
                </div>
                <div>
                  <button 
                  className={`btn btn-${bg}`}
                  id="new-quote" onClick={ this.getNextQuote }>
                    New Quote
                  </button>
                </div>
              </div>
          </div>

          <footer id="footer" className="">
            <p>by Musekwa</p>
          </footer>
      </div>
    );
  }
}
